mapper.insert:

 SELECT SEQ_USER.NEXTVAL FROM DUAL

INSERT INTO TB_USER(
USER_ID
,LOGIN_ID
,LOGIN_PW
,NAME
,PHONE
,EMAIL
)
VALUES
(
#{userId}
,#{loginId}
,#{loginPw}
,#{name}
,#{phone}
,#{email}
)

mapper.select:
SELECT COUNT(*) AS CNT
FROM TB_USER
WHERE PHONE = #{phone}

SELECT COUNT(*) AS CNT
FROM TB_USER
WHERE EMAIL = #{email}
